import React, { Component } from "react";

class Experience extends Component {
  constructor() {
    super();

    this.state = {
      title: "Experience",
      button: "+ Experience",
      experienceForm: "",
      experienceList: "",
      experienceArray: [],
    };
  }
  handleExperienceForm = () => {
    this.setState({
      experienceForm: (
        <form className="experienceForm" onSubmit={this.handleSubmit}>
          <label htmlFor="company">Company: </label>
          <input type="text" placeholder="Company Name" name="company"></input>
          <br></br>
          <label htmlFor="city">City: </label>
          <input type="text" placeholder="City" name="city"></input>
          <br></br>
          <label htmlFor="from">From: </label>
          <input type="text" placeholder="From" name="from"></input>
          <label htmlFor="from">To: </label>
          <input type="text" placeholder="To" name="to"></input>
          <br></br>
          <label htmlFor="role">Role: </label>
          <input type="text" placeholder="Role" name="role"></input>
          <br></br>
          <label htmlFor="duties">Job Duties: </label>
          <textarea
            placeholder="Job Duties"
            name="duties"
            rows="8"
            cols="70"
          ></textarea>
          <button type="submit">Submit</button>
          <button>Cancel</button>
        </form>
      ),
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
      experienceList: {
        company: e.target.c.company,
        city: this.state.city,
        from: this.state.from
      },
      experienceForm: "",
    });
    this.setState(previousState => ({
        experienceArray: [...previousState.experienceArray, this.state.experienceList]
    }))
    console.log(this.state.experienceArray)
  };

  render() {
    return (
      <div className="generalInfoContainer">
        <h1 className="generalHeader">{this.state.title}</h1>
        <button onClick={this.handleExperienceForm} className="generalButton">
          {this.state.button}
        </button>
        <div>{this.state.experienceForm}</div>
        <div>{this.state.experienceList}</div>
      </div>
    );
  }
}

export default Experience;
