import React from 'react';
import ContactInfo from './ContactInfo.js'

const Header = (props) => {
  const displayInput = (e) => {
     e.target.textContent = "";
     const input = document.createElement("input");
     input.type = "text";
     input.className = "firstNameInput";
     input.placeholder = "First Name";
     const firstNameContainer = document.querySelector(".firstNameContainer");
     firstNameContainer.appendChild(input);
  }

  //const removeInput = (e) => {
  //  console.log(e.target);
  //}
  //window.onClick


    window.addEventListener("click" , (e) => {
    console.log(e.target)
     console.log("node name" , e.target.nodeName)
     console.log("class name" , e.target.className)
     const input = document.querySelector(".firstNameInput");
     const firstNameContainer = document.querySelector(".firstNameContainer");
     const firstName = document.querySelector(".firstName");
     if (input && e.nodeName !== "INPUT" && e.target.className !== "firstName") {
     firstNameContainer.removeChild(input);
     firstName.textContent = "First Name"
     }



  })
    return (
        <div className="headerContainer">
            <div className="left">
                <div className="firstNameContainer">
                    <h1 onClick = {displayInput} className="firstName">{props.firstName}</h1>
                </div>
                <div className="lastNameContainer">
                    <h1 className="lastName">{props.lastName}</h1>
                </div>
                <div className="currentRoleContainer">
                    <span className="currentRole">{props.currentRole}</span>
                </div>
            </div>
            <div className="right">
                <ContactInfo addressOne="Address 1" addressTwo="Address 2" phone="Phone" email="Email" linkedin="Linkedin" />
            </div>
        </div>
    )
  };

  export default Header;
