import React, { Component } from 'react';

class ContactInfo extends Component {
    constructor() {
      super();

      this.state = {
        addressOne: "Address 1",
        addressTwo: "Address 2",
        phone: "Phone Number",
        email: "Email",
        linkedin: "Linkedin"
      };
    }

    displayAddressOneInput = () => {
        this.setState({
          addressOne: (
            <input
              type="text"
              placeholder="Address 1"
              name="addressOne"
              onKeyDown={this.handleAddressOneKeyDown}
              onBlur={this.handleAddressOneBlur}
            ></input>
          ),
        });
      };

      handleFAddressOneBlur = (e) => {
        this.setState({
          addressOne: e.target.value
        })
      }

      handleAddressOneDown =(e) => {
        if (e.key === 'Enter') {
          this.setState({
            addressOne: e.target.value
          })
        }
      }


    render() {
    return (
        <div className="contactContainer">
            <h4 onClick={this.displayAddressOneInput}>{this.state.addressOne}</h4>
            <h4>{this.state.addressTwo}</h4>
            <p>{this.state.phone}</p>
            <p>{this.state.email}</p>
            <p>{this.state.linkedin}</p>
        </div>
    )
    }
  };

  export default ContactInfo;
