import React, {useState} from "react";

const DisplayExperience = (props) => {
  const { experienceArray } = props;
  const [closeButton, setDisplay] = useState("notdisplayed");
  const showButton = () => {

    setDisplay("displayed");
  };

  const hideButton = () => {

    setDisplay("notdisplayed");
  };


  return (
    <div>
      {experienceArray.map((arr) => {
        return (
          <div key={arr.id} className="workExperience">
            <div className="leftSideExperience" onMouseEnter={this.showButton}
          onMouseLeave={e => this.hideButton}>

              <br></br>
              <div>{arr.company}</div>
              <div>{arr.role}</div>
              <br></br>
              <div>{arr.duties}</div>
            </div>
            <div className="roleAndDuties">
              <div>{arr.experienceFrom} - {arr.experienceTo}</div>
              <div>{arr.experienceCity}</div>
            </div>
            <div className={closeButton}>X</div>
            <br></br>

          </div>
        );
      })}
    </div>
  );
};

export default DisplayExperience;
