import React, { Component } from "react";
import ContactInfo from "./ContactInfo.js";

class Header extends Component {
  constructor() {
    super();

    this.state = {
      firstName: "First Name",
      lastName: "Last Name",
      currentRole: "Current Role",
      input: true,
    };
  }

  displayFirstNameInput = () => {
    this.setState({
      firstName: (
        <input
          type="text"
          placeholder="First Name"
          name="firstName"
          onKeyDown={this.handleKeyDown}
          onBlur={this.handleBlur}
        ></input>
      ),
    });
  };

  handleBlur = (e) => {
    this.setState({
      firstName: e.target.value
    })
  }

  handleKeyDown =(e) => {
    if (e.key === 'Enter') {
      this.setState({
        firstName: e.target.value
      })
    }
  }

  render() {
    return (
      <div className="headerContainer">
        <div className="left">
          <div className="firstNameContainer">
            <h1 onClick={this.displayFirstNameInput} className="firstName">
              {this.state.firstName}
            </h1>
          </div>
          <div className="lastNameContainer">
            <h1 onClick={this.displayLastNameInput} className="lastName">{this.state.lastName}</h1>
          </div>
          <div className="currentRoleContainer">
            <span className="currentRole">{this.state.currentRole}</span>
          </div>
        </div>
        <div className="right">
          <ContactInfo
            addressOne="Address 1"
            addressTwo="Address 2"
            phone="Phone"
            email="Email"
            linkedin="Linkedin"
          />
        </div>
      </div>
    );
  }
}

export default Header;
